import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{f as e}from"./app.6199cf76.js";const r={},h=e('<p>\u4E0B\u9762\u662F\u6839\u636E\u5927\u5BB6\u7684\u5E38\u89C1\u95EE\u9898\uFF0C\u5BF9\u5DE5\u4F5C\u5BA4\u76F8\u5173\u60C5\u51B5\u8FDB\u884C\u7684\u89E3\u7B54\u3002</p><h2 id="\u975E\u6B63\u5F0F\u793E\u56E2\u4E0E\u516C\u53F8" tabindex="-1"><a class="header-anchor" href="#\u975E\u6B63\u5F0F\u793E\u56E2\u4E0E\u516C\u53F8" aria-hidden="true">#</a> \u975E\u6B63\u5F0F\u793E\u56E2\u4E0E\u516C\u53F8</h2><p>\u5DE5\u4F5C\u5BA4\u5E76\u4E0D\u662F\u4E00\u4E2A\u6B63\u5F0F\u793E\u56E2\uFF0C\u6362\u53E5\u8BDD\u8BF4<strong>\u672A\u5728\u5B66\u6821\u6CE8\u518C</strong>\uFF0C\u540C\u65F6\u5DE5\u4F5C\u5BA4\u4E5F\u5E76\u4E0D\u662F\u4E00\u4E2A\u6B63\u5F0F\u7684\u516C\u53F8\u3002</p><blockquote><p>\u539F\u5219\u4E0A\u662F\u53EF\u4EE5\u6CE8\u518C\u5B66\u6821\u793E\u56E2\u7684\uFF0C\u4F46\u662F\u624B\u7EED\u548C\u6750\u6599\u592A\u9EBB\u70E6\u4E86\uFF0C\u800C\u4E14\u4E24\u4E2A\u5BA4\u957F\u4E5F\u4E0D\u662F\u592A\u5728\u4E4E\u662F\u5426\u662F\u4E00\u4E2A\u6B63\u5F0F\u793E\u56E2</p><p>\u53E6\u5916\u7531\u4E8E\u5E76\u6CA1\u6709\u6B63\u5F0F\u6CE8\u518C\u516C\u53F8\uFF0C\u6240\u4EE5\u6CA1\u6709\u516C\u7AE0\u3002</p></blockquote><h2 id="\u7EBF\u4E0A\u534F\u4F5C\u4E3A\u4E3B" tabindex="-1"><a class="header-anchor" href="#\u7EBF\u4E0A\u534F\u4F5C\u4E3A\u4E3B" aria-hidden="true">#</a> \u7EBF\u4E0A\u534F\u4F5C\u4E3A\u4E3B</h2><p>\u76EE\u524D\u7531\u4E8E\u5DE5\u4F5C\u5BA4\u5305\u542B\u4E24\u4E2A\u6821\u533A\u7684\u4EBA\uFF0C\u4E14\u5BA4\u957F\u4E0D\u5728\u5B66\u6821\uFF0C\u6240\u4EE5\u5DE5\u4F5C\u5BA4\u4E3B\u8981\u7684\u5DE5\u4F5C\u3001\u57F9\u8BAD\u90FD\u91C7\u53D6<strong>\u7EBF\u4E0A\u7684\u5F62\u5F0F</strong>\u3002</p><blockquote><p>\u6821\u5185\u540C\u7C7B\u793E\u56E2\u4ECB\u7ECD</p><p>\u672C\u90E8\u6821\u533A\u7684\u5353\u97F3\u3001E \u7FD4\u662F\u6B63\u5F0F\u7684\u793E\u56E2\uFF0C\u80CC\u9760\u5C31\u4E1A\u6307\u5BFC\u4E2D\u5FC3\u548C\u4FE1\u606F\u5316\u529E\u516C\u5BA4\u3002\u4ED6\u4EEC\u4E5F\u6CA1\u6709\u95E8\u69DB\uFF0C\u800C\u4E14\u5F88\u8F7B\u677E\u3002\u4F46\u662F\u4ED6\u4EEC\u7684\u57F9\u8BAD\u7684\u5185\u5BB9\u8FD8\u505C\u7559\u5728 2005 \u5E74\u7684\u6C34\u5E73\uFF0C\u76F4\u767D\u70B9\u8BF4\u5C31\u662F\u4F60\u52A0\u4E86\u5B66\u4E0D\u5230\u4EC0\u4E48\u3002</p><p>\u51C0\u6708\u6821\u533A\u4FE1\u606F\u79D1\u5B66\u4E0E\u6280\u672F\u5B66\u9662\u6709\u4E00\u4E24\u4E2A\u8DDF\u672C\u5DE5\u4F5C\u5BA4\u76F8\u8FD1\u7684\u5DE5\u4F5C\u5BA4\uFF0C\u4E3B\u8981\u4EE5\u63A5\u5916\u754C\u9879\u76EE\u5B9E\u884C\u76C8\u5229\uFF0C\u51C6\u5165\u95E8\u69DB\u5F88\u9AD8\uFF0C\u8981\u6C42\u65B0\u52A0\u5165\u6210\u5458\u6709\u5BF9\u5E94\u7684\u80FD\u529B\u3002</p></blockquote><h2 id="\u53CC\u65B9\u5173\u7CFB" tabindex="-1"><a class="header-anchor" href="#\u53CC\u65B9\u5173\u7CFB" aria-hidden="true">#</a> \u53CC\u65B9\u5173\u7CFB</h2><h3 id="\u5DE5\u4F5C\u5BA4\u7ED9\u4E88\u7684\u5E2E\u52A9" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u5BA4\u7ED9\u4E88\u7684\u5E2E\u52A9" aria-hidden="true">#</a> \u5DE5\u4F5C\u5BA4\u7ED9\u4E88\u7684\u5E2E\u52A9</h3><p>\u5DE5\u4F5C\u5BA4\u8D1F\u8D23\u57F9\u517B\u5927\u5BB6\uFF0C\u6839\u636E\u5927\u5BB6\u6240\u9009\u7684\u65B9\u5411\uFF0C\u5C06\u62DB\u65B0\u8BF4\u660E\u91CC\u7684\u5404\u9879\u5185\u5BB9\u90FD\u6559\u5BFC\u7ED9\u5927\u5BB6\u3002\u540C\u65F6\u5DE5\u4F5C\u5BA4\u8FD9\u9762\u4E5F\u6709\u4E00\u4E9B\u9879\u76EE\u53EF\u4EE5\u4F9B\u5927\u5BB6\u7EC3\u4E60\u4E0E\u5B9E\u8DF5\u3002</p><p>\u76EE\u524D\u5DE5\u4F5C\u5BA4\u4E5F\u53EF\u4EE5\u63D0\u4F9B\u4E00\u4E9B\u5927\u5382\u7684\u5185\u90E8\u63A8\u8350\u540D\u989D\uFF0C\u4F46\u662F\u5177\u4F53\u80FD\u4E0D\u80FD\u8D70\u4E0A\u9700\u8981\u770B\u4F60\u7684\u4E2A\u4EBA\u80FD\u529B\u3002</p><blockquote><p>\u900F\u4E2A\u5E95\uFF0C\u73B0\u5728\u5B57\u8282\u8DF3\u52A8\u3001\u817E\u8BAF\u7684\u5185\u90E8\u63A8\u8350\u540D\u989D\u662F\u53EF\u4EE5\u7ED9\u5927\u5BB6\u7684\uFF0C\u4E0D\u9A97\u4EBA\u3002\u4F46\u662F\u4E0D\u4FDD\u8BC1\u5927\u5BB6\u6BD5\u4E1A\u65F6\u53EF\u4EE5\u62FF\u5230\u63A8\u8350\u540D\u989D\u3002</p></blockquote><h3 id="\u5DE5\u4F5C\u5BA4\u7684\u9700\u6C42" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u5BA4\u7684\u9700\u6C42" aria-hidden="true">#</a> \u5DE5\u4F5C\u5BA4\u7684\u9700\u6C42</h3><p>\u5927\u5BB6\u9700\u8981\u5E2E\u52A9\u5DE5\u4F5C\u5BA4\u53BB\u5B8C\u6210\u4E00\u4E9B\u5185\u90E8\u7684\u9879\u76EE\u5DE5\u4F5C\uFF0C\u6BD4\u5982\u627F\u63A5\u7684\u5B66\u6821\u76F8\u5173\u7684\u9879\u76EE\u3002</p><h3 id="\u5176\u4ED6" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6" aria-hidden="true">#</a> \u5176\u4ED6</h3><p>\u5DE5\u4F5C\u5BA4\u7684\u4E00\u4E9B\u65B0\u9879\u76EE\uFF0C\u53EF\u4EE5\u8003\u8651\u627F\u63A5\u5B66\u6821\u76F8\u5173\u673A\u6784\u7684\u4E00\u4E9B\u9700\u6C42\uFF0C\u4E5F\u53EF\u4EE5\u8003\u8651\u627F\u63A5\u6821\u5916\u7684\u4E00\u4E9B\u9879\u76EE\uFF0C\u4E5F\u53EF\u4EE5\u662F\u5927\u5BB6\u4E00\u8D77\u8BA8\u8BBA\u4E00\u4E2A\u90FD\u6BD4\u8F83\u611F\u5174\u8DA3\u3001\u6709\u8D62\u5229\u70B9\u7684\u9879\u76EE\u81EA\u5DF1\u505A\u3002\u4EE5\u5927\u5BB6\u7684\u5174\u8DA3\u548C\u610F\u89C1\u4E3A\u4E3B\u3002</p><h2 id="\u65E5\u5E38\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u65E5\u5E38\u5185\u5BB9" aria-hidden="true">#</a> \u65E5\u5E38\u5185\u5BB9</h2><h3 id="\u5B66\u4E60\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u5B66\u4E60\u9636\u6BB5" aria-hidden="true">#</a> \u5B66\u4E60\u9636\u6BB5</h3><p>\u524D 4 - 6 \u4E2A\u6708\u5C06\u4E3B\u8981\u4EE5 <strong>\u81EA\u5B66-\u7B54\u7591-\u7EC3\u4E60</strong>\u3001<strong>\u7EDF\u4E00\u57F9\u8BAD\u8BB2\u89E3\u7591\u96BE\u70B9+\u603B\u7ED3</strong> \u7684\u5F62\u5F0F\u8FDB\u884C\u3002\u8FD9\u4E2A\u9636\u6BB5\u4F1A\u5F88\u67AF\u71E5\uFF0C\u800C\u4E14\u9700\u8981\u5927\u5BB6\u6295\u5165\u6BD4\u8F83\u591A\u7684\u65F6\u95F4\u3002</p><p>\u7531\u4E8E\u5927\u5BB6\u6765\u81EA\u4E0D\u540C\u7684\u5B66\u9662\uFF0C\u65F6\u95F4\u5B89\u6392\u4E5F\u4E0D\u540C\uFF0C\u6240\u4EE5\u5E73\u65F6\u5927\u5BB6\u4E3B\u8981\u81EA\u4E3B\u5B66\u4E60\uFF0C\u9047\u5230\u95EE\u9898\u968F\u65F6\u95EE\u4E24\u4F4D\u5BA4\u957F\uFF0C\u4E24\u4F4D\u5BA4\u957F\u8D1F\u8D23\u4E3A\u5927\u5BB6\u63D0\u4F9B\u7B54\u7591\u3002\u5BF9\u4E8E\u4E00\u4E9B\u96BE\u70B9\u3001\u7591\u95EE\u6BD4\u8F83\u591A\u7684\u5730\u65B9\uFF0C\u4EE5\u53CA\u77E5\u8BC6\u7684\u9636\u6BB5\u6027\u603B\u7ED3\uFF0C\u4F1A\u5728\u5468\u672B\u534F\u8C03\u4E00\u4E2A 1 - 2 \u5C0F\u65F6\u7684\u65F6\u95F4\u901A\u8FC7\u817E\u8BAF\u4F1A\u8BAE\u8FDB\u884C\u7EDF\u4E00\u7684\u57F9\u8BAD\u3001\u8BB2\u89E3\u4E0E\u603B\u7ED3\u3002</p><p>\u5728\u5B66\u4E60\u9636\u6BB5\uFF0C\u4F1A\u5B89\u6392\u4E00\u4E9B\u914D\u5957\u7EC3\u4E60\u5E2E\u52A9\u5927\u5BB6\u7406\u89E3\u77E5\u8BC6\uFF0C\u4E5F\u6B22\u8FCE\u5927\u5BB6\u9488\u5BF9\u5B66\u4E60\u9636\u6BB5\u7684\u5185\u5BB9\u81EA\u884C\u8BBE\u8BA1\u4E00\u4E9B\u5C0F\u7684\u9879\u76EE\uFF0C\u6BD4\u5982\u5728\u5B8C\u6210\u9759\u6001\u7F51\u7AD9\u5B66\u4E60\u540E\uFF0C\u81EA\u5DF1\u8BBE\u8BA1\u4E00\u4E2A\u4E2A\u4EBA\u7F51\u7AD9\u4E3B\u9875\uFF0C\u5728\u5B66\u4E60\u5B8C JS \u540E\u81EA\u5DF1\u8BBE\u8BA1\u4E00\u4E2A\u5C0F\u6E38\u620F\u7B49\u3002</p><h3 id="\u534F\u4F5C\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u534F\u4F5C\u9636\u6BB5" aria-hidden="true">#</a> \u534F\u4F5C\u9636\u6BB5</h3><p>\u5728\u5B8C\u6210\u5927\u81F4\u5185\u5BB9\u7684\u5B66\u4E60\u540E\uFF0C\u5927\u5BB6\u5C31\u53EF\u4EE5\u8FB9\u5B66\u4E60\u3001\u8FB9\u7EC3\u4E60\u3001\u8FB9\u63D0\u9AD8\u3002\u8FD9\u4E2A\u9636\u6BB5\u5E0C\u671B\u5927\u5BB6\u80FD\u591F\u4E00\u8D77\u60F3\u4E00\u4E2A\u8FD8\u4E0D\u9519\u7684\u9879\u76EE\uFF0C\u4EE5\u56E2\u961F\u534F\u4F5C\u7684\u65B9\u5F0F\uFF0C\u53BB\u5B8C\u6210\u8FD9\u4E2A\u9879\u76EE\uFF0C\u5E76\u5728\u5B8C\u6210\u9879\u76EE\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u89E3\u51B3\u9047\u5230\u7684\u96BE\u9898\uFF0C\u4E0D\u65AD\u63D0\u9AD8\u81EA\u5DF1\u7684\u6C34\u5E73\u3002</p><h3 id="\u5DE5\u4F5C\u9636\u6BB5" tabindex="-1"><a class="header-anchor" href="#\u5DE5\u4F5C\u9636\u6BB5" aria-hidden="true">#</a> \u5DE5\u4F5C\u9636\u6BB5</h3><p>\u7B49\u5230\u5927\u5BB6\u5DF2\u7ECF\u5145\u5206\u901A\u8FC7\u9879\u76EE\u7684\u5B9E\u8DF5\u63D0\u5347\u4E86\u81EA\u5DF1\u7684\u80FD\u529B\u4E0E\u6548\u7387\uFF0C\u5927\u5BB6\u5C31\u53EF\u4EE5\u6839\u636E\u81EA\u8EAB\u7684\u9700\u6C42\uFF0C\u5982\u679C\u4ECD\u7136\u5F88\u611F\u5174\u8DA3\u5E76\u4E14\u6709\u610F\u4ECE\u4E8B\u76F8\u5173\u9886\u57DF\u5DE5\u4F5C\u7684\uFF0C\u53EF\u4EE5\u7EE7\u7EED\u8FDB\u884C\u6DF1\u5165\u7684\u5B66\u4E60\uFF0C\u5927\u5BB6\u4E5F\u53EF\u4EE5\u627F\u63A5\u4E00\u4E9B\u5B66\u6821\u6216\u5916\u90E8\u7684\u9879\u76EE\u6765\u5B9E\u73B0\u4E00\u4E9B\u76C8\u5229\u3002(\u6211\u53EF\u4EE5\u5E2E\u5FD9\u627E)</p><blockquote><p><strong>\u8865\u5145\u8BF4\u660E:</strong></p><p>\u5927\u5BB6\u7684\u57FA\u7840\u4E0D\u540C\u3001\u65B9\u5411\u4E0D\u540C\uFF0C\u4E00\u4E9B\u4EBA\u4E3A\u8BA1\u7B97\u673A\u76F8\u5173\u4E13\u4E1A\u6216\u6709\u57FA\u7840\uFF0C\u6240\u4EE5\u8FDB\u5165\u5230\u534F\u4F5C\u9636\u6BB5\u540E\uFF0C\u5C06\u4E0D\u518D\u6709\u7EDF\u4E00\u7684\u57F9\u8BAD\u6216\u8005\u7B54\u7591\uFF0C\u5927\u5BB6\u6709\u95EE\u9898\u53EF\u4EE5\u76F4\u63A5\u53D1\u5230\u7FA4\u91CC\u8FDB\u884C\u4EA4\u6D41\u3002</p></blockquote><h2 id="\u4E00\u4E9B\u8B66\u544A" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E9B\u8B66\u544A" aria-hidden="true">#</a> \u4E00\u4E9B\u8B66\u544A</h2><p>\u5DE5\u4F5C\u5BA4\u7684\u6BCF\u6761\u8981\u6C42\u90FD\u662F\u6709\u610F\u4E49\u7684\u3002\u5DE5\u4F5C\u5BA4\u5728\u62DB\u65B0\u4E2D\uFF0C\u627F\u8BFA\u4E86\u5F88\u591A\u76F8\u5173\u7684\u6280\u80FD\uFF0C\u4F46\u662F\u540C\u65F6\u4E5F\u8BF7\u5927\u5BB6\u6CE8\u610F\uFF0C\u8FD9\u4E9B\u76F8\u5173\u6280\u80FD\u4E5F\u5E76\u4E0D\u80FD\u76F4\u63A5\u8FDB\u5165\u5230\u5927\u5BB6\u7684\u8111\u888B\u4E2D\uFF0C\u540C\u65F6\u5F00\u53D1\u4E5F\u662F\u9700\u8981\u5927\u91CF\u5B9E\u8DF5\uFF0C\u624D\u80FD\u4E0D\u65AD\u8E29\u5751\u3001\u907F\u5751\uFF0C\u5199\u51FA\u4F18\u79C0\u7684\u4EE3\u7801\u3001\u7F8E\u89C2\u7684\u754C\u9762\u7684\u3002</p><p>\u6240\u4EE5\u9996\u5148\u4F60\u9700\u8981\u505A\u597D\u6295\u5165\u6BD4\u8F83\u591A\u7684\u65F6\u95F4\u7684\u51C6\u5907\uFF0C\u6CA1\u6709\u4EFB\u4F55\u7684\u6280\u80FD\u662F\u53EF\u4EE5\u4E0D\u52AA\u529B\u51ED\u7A7A\u83B7\u5F97\u7684\u3002\u53E6\u5916\u7531\u4E8E\u5B66\u4E60\u5468\u671F\u5F88\u957F\uFF0C\u8BF7\u786E\u4FDD\u4F60\u6709\u8DB3\u591F\u575A\u5B9A\u7684\u610F\u5FD7\u6216\u662F\u6709\u8DB3\u591F\u7684\u5174\u8DA3\u3002\u540C\u65F6\uFF0CMr.Hope \u5468\u56F4\u8BA4\u8BC6\u7684\u6BCF\u4E00\u4E2A\u5927\u4F6C\uFF0C\u5373\u4F7F\u662F\u76F8\u5173\u4E13\u4E1A\u7684\uFF0C\u4E5F\u662F\u8981\u9760\u8BFE\u7A0B\u4E4B\u5916\u4E0D\u65AD\u81EA\u5B66\u3001\u4E0D\u65AD\u7EC3\u4E60\u3001\u4E0D\u65AD\u5B9E\u8DF5\u7684\uFF0C\u4E00\u5207\u9760\u81EA\u4E3B\uFF0C\u6240\u4EE5\u5927\u5BB6\u4E0D\u8981\u6307\u671B\u57F9\u8BAD\u548C\u88AB\u52A8\u704C\u8F93\u7684\u8BDD\uFF0C\u4E00\u5207\u8981\u9760\u81EA\u4E3B\u3002\u6362\u8A00\u4E4B\uFF0C\u53EA\u6709\u4F60\u6301\u7EED\u6295\u5165\uFF0C\u62FF\u51FA\u8DB3\u591F\u591A\u7684\u65F6\u95F4\uFF0C\u81EA\u4E3B\u53BB\u5B66\u4E60\uFF0C\u624D\u80FD\u62FF\u5230\u4E00\u4E2A\u597D\u7ED3\u679C\u3002</p><p>\u53E6\u5916\uFF0C\u7F16\u7A0B\u9700\u8981<strong>\u903B\u8F91\u601D\u7EF4</strong>\u80FD\u529B\uFF0C\u8BBE\u8BA1\u9700\u8981<strong>\u5BA1\u7F8E</strong>\u80FD\u529B\uFF0C\u540E\u7AEF\u4E0D\u4EC5\u9700\u8981<strong>\u5F3A\u5927\u7684\u903B\u8F91</strong>\u3001\u8FD8\u9700\u8981<strong>\u7F1C\u5BC6\u7684\u601D\u7EF4</strong>\u3002\u6839\u636E Mr.Hope \u7684\u611F\u89E6\uFF0C\u8FD9\u4E9B\u6709\u4E00\u90E8\u5206\u5148\u5929\u56E0\u7D20\u548C\u4E4B\u524D\u7684\u79EF\u7D2F\u3002\u8BF7\u914C\u60C5\u8003\u8651\u53D1\u5C55\u7684\u65B9\u5411\u3002\u6BD4\u5982\u4F60\u771F\u7684\u662F\u76F4\u7537\u5BA1\u7F8E\u7684\u8BDD\uFF0C\u4E0D\u8981\u53BB\u9009\u4EA7\u54C1\u8BBE\u8BA1\uFF0C\u5927\u5BB6\u90FD\u4F1A\u5F88\u7EDD\u671B\u3002</p><hr><p>\u53E6\u5916\uFF0C\u672C\u5DE5\u4F5C\u5BA4\u5E76\u4E0D\u662F\u4E00\u4E2A\u6269\u5145\u81EA\u5DF1\u6280\u80FD\u4E3A\u4E3B\u7684\u5730\u65B9\u3002\u5982\u679C\u4F60\u6709\u4ECE\u4E8B\u76F8\u5173\u9886\u57DF\u5DE5\u4F5C\u7684\u60F3\u6CD5\uFF0C\u6216\u662F\u539F\u6765\u5C31\u60F3\u62A5\u8003\u8BA1\u7B97\u673A\u3001\u5BF9\u76F8\u5173\u5185\u5BB9\u5F88\u611F\u5174\u8DA3\uFF0CMr.Hope \u5EFA\u8BAE\u4F60\u9009\u62E9\u3002</p><p>\u4F46\u662F\u5982\u679C\u4F60\u662F\u60F3\u5355\u7EAF\u57F9\u517B\u4E00\u4E2A\u989D\u5916\u6280\u80FD\u7684\u8BDD\uFF0C\u9009\u62E9\u672C\u5DE5\u4F5C\u5BA4\uFF0C\u4F60\u4F1A\u6295\u5165\u5927\u91CF\u7684\u65F6\u95F4\uFF0C\u8FC7\u540E\u4F60\u5B66\u5230\u7684\u5185\u5BB9\u4E5F\u5F88\u5927\u6982\u7387\u4F1A\u8352\u5E9F\u3001\u5F97\u4E0D\u507F\u5931\u3002</p>',33);function t(n,o){return h}var i=a(r,[["render",t],["__file","notice.html.vue"]]);export{i as default};
